import React, { Component } from 'react';
import ScrollUpButton from "react-scroll-up-button";
import config from './config.js'
const firebase = require('firebase');

const sample = ["hi", "hello", "how are you"];
var notGonnaWork;
export class Messages extends Component {

	constructor(props) {
		super(props)
		this.state = {data: {}}
		console.log("constructor")
	}
	componentDidMount(){
		firebase.initializeApp(config)
		let ref = firebase.database().ref('data')
		console.log(ref)
		ref.on('value', snapshot => {
			const data = snapshot.val()
			Object.values(this.state).map((index,s) => (
					console.log(s)
					))
			// console.log(Object.values)
			this.setState({data: data})
			notGonnaWork = this.state
		})
	}
	componentDidUpdate(prevProps, prevState, snapshot){
		console.log("update?")
		//only call set state here if it is wrapped in a condition
		//if you initialize this.state.shouldUpdate and have not changed it yet then this will not run
		if(this.state.shouldUpdate !== prevState.shouldUpdate){
			const data = snapshot.val()
			this.setState({data: data})
			notGonnaWork = this.state
			//same code as above to retrieve the data 
		}
	}

	render() {
		return (
			<div>
				<header>
					<ScrollUpButton />
					<h1>Messages</h1>
						{
							console.log(Object.values(this.state))
						}
						{Object.values(this.state).map((index,s) => (
							<p>
								{s.value}
								}
							</p>
							))}
					</header>

				</div>
			);
	}
}
export default Messages;
