import React, { Component } from 'react';
import github1 from '../Images/github.png';
import ScrollUpButton from "react-scroll-up-button";
import { SRLWrapper } from 'simple-react-lightbox';
import ReactDOM from 'react-dom'
import config from './config.js'
const firebase = require('firebase');
const axios = require('axios');

// firebase.initializeApp(config)
// var ref = firebase.database().ref('movies')

var movieIDs = [
		"tt4633694",
		"tt0091419",
		"tt2582802",
		"tt2091935",
		"tt3416742",
		"tt0432283",
		"tt1465522",
		"tt0119116",
		"tt0446029",
		"tt0211915",
		"tt0097493",
		"tt0780536"
		];

var images = [];

var databaseMovies = [];

// var ref = firebase.database().ref('movies')

function updateMovies() {

	images = []
	var codeBlock = ''
	databaseMovies.forEach(element => 
	axios.get("https://www.omdbapi.com/?apikey=71e07b98&i=" + element).then(function(response) {
		console.log(response);
		var img = new Image();
		var url = response.data.Poster;
		img.src = url;
		img.caption = "hello!"
		images.push(img)
		console.log(url);

		codeBlock = codeBlock + '<div class="container"><img src =' + url + ' class = "image"/><div class = "middle"><div class="text"></div></div></div>'

	}).then(function(response) {
		document.getElementById("innerArray").innerHTML = codeBlock

	}).catch(function(error) {
	  	console.error(error)
	})
	);
};

function testing() {
	var ref = firebase.database().ref('movies');
	databaseMovies = []

	console.log("NOTICE ME")
	ref.once('value',function(snap) {
    snap.forEach(function(item) {
        var itemVal = item.key;
        databaseMovies.push(itemVal);
    });
    console.log(databaseMovies.length)
//     for (var i=0; i < databaseMovies.length; i++) {

//     	console.log(databaseMovies[i])
// //         counts.push(keys[i].wordcount);
//     }   
}).then(function(response) {
	updateMovies();
});
}

export class Movies extends Component {



	render() {

		testing()

		return (
			<div>
				<ScrollUpButton />
				<h1>Movies</h1>

					<SRLWrapper> 
					<div id = "innerArray">
					</div>
					</SRLWrapper>
				</div>
			);
	}
}

export default Movies;
